import React from 'react'
import { Tab, Tabs, TabList, TabPanel } from 'react-tabs';
import Card1 from './Card2'
import Card2 from './Card2'
import Card3 from './Card3'
import $ from 'jquery'

const Cards = () => {
const selectTab = element => {
  const active = document.querySelector('.active');
  const visible = document.querySelector('.content-visible');
  const tabContent = document.getElementById(element.href.split('#')[1]);
  if (active) {
    active.classList.remove('active');
  }
  element.classList.add('active');
  if (visible) {
    visible.classList.remove('content-visible');
  }
  tabContent.classList.add('content-visible');
}
document.addEventListener('click', event => {
  if (event.target.matches('.tab-item a')) {
    selectTab(event.target);
  }
}, false);
    return (
      <>
 <section className="my-5">
<div className="container text-center">
  <ul className="tabs row bg-l-grey p-2 rounded-pill fw-bolder mx-5 text-center">
    <li className="tab-item col-lg-6 col-md-6 col-sm-12 p-2"><a href="#item1" className="text-decoration-none w-100 active px-5">SSD Hosting</a></li>
    <li className="tab-item col-lg-6 col-md-6 col-sm-12 p-2"><a href="#item3" className="text-decoration-none w-100 px-5">Flash Hosting</a></li>
  </ul>
  <div className="wrapper_tab-content">
    <article id="item1" className="tab-content content-visible">
<Card1/>
    </article>
    <article id="item3" className="tab-content">
<Card3/>
    </article>
  </div>
  </div>
</section>
      </>
    )
}

export default Cards
