import React from "react";
import { Switch } from "react-router";
import {
  BrowserRouter as Router,
  Route,
  Link
} from "react-router-dom";
import { Helmet } from 'react-helmet';

// navbar images 
import Logo from '../src/images/logo topbar.jpeg';

// pages 
import Domain from './Components/Domain';
import HomePage from './Components/Main';
import Shared from './Components/Shared';
import AffiliateHome from "./Components/AffiliateHome";
import ResellerMain from "./Components/ResellerMain";
import DedicateMain from "./Components/Dedicate";
import VpsMain from './Components/VpsHome';
import Windows from './Components/Windows';

export default function App() {

  // <!--Start of Tawk.to Script-->
  var Tawk_API = Tawk_API || {}, Tawk_LoadStart = new Date();
  (function () {
    var s1 = document.createElement("script"), s0 = document.getElementsByTagName("script")[0];
    s1.async = true;
    s1.src = 'https://embed.tawk.to/5d7e91079f6b7a4457e1cadb/default';
    s1.charset = 'UTF-8';
    s1.setAttribute('crossorigin', '*');
    s0.parentNode.insertBefore(s1, s0);
  })();
  // <!--End of Tawk.to Script-->

  return (
    <Helmet>
      <meta property="og:image" content={Logo} />
    </Helmet>,
    <Router>
      <>

        <nav>
          {/* topbar  */}
          <div className="container-fluid row bg-secondary m-0 text-light">
            <div className="container m-0 p-0">
              <div className="row m-0 p-0">
                <div className="col-lg-6 col-md-6 col-sm-12">
                  <img src={Logo} className="" />
                </div>
                <div className="col-lg-6 col-md-6 col-sm-12 ">
                  <div className="row">
                    <p className="col-1 top-pra fw-bolder"> <i className="fas fa-phone-alt"></i> </p>
                    <p className="col-1 top-pra fw-bolder"> <i className="fab fa-whatsapp"></i> </p>
                    <a className="col-1 text-decoration-none text-light" href="https://www.facebook.com/hoster.pk" target="_blank"><p className="mx-2 py-0 my-0 top-pra"><i className="fab fa-facebook-f"></i></p></a>
                    <a className="col-1 text-decoration-none text-light" href="https://www.youtube.com/channel/UCY1GvZd8wY1B-PbsvbuSZOg" target="_blank"><p className="mx-2 py-0 my-0 top-pra"><i className="fab fa-youtube"></i></p></a>
                    <a href="col-1 https://pk.linkedin.com/company/pitspk" className="text-white" target="_blank"><p className="mx-2 py-0 my-0 top-pra"><i className="fab fa-linkedin-in"></i></p></a>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </nav>


        <Switch>
          <Route path="/windows">
            <Window />
          </Route>
          <Route path="/dedicate">
            <Dedicate />
          </Route>
          <Route path="/vps">
            <VPS />
          </Route>
          <Route path="/affiliate">
            <Affiliate />
          </Route>
          <Route path="/reseller">
            <Reseller />
          </Route>
          <Route path="/domain">
            <Domain />
          </Route>
          <Route path="/hosting">
            <Hosting />
          </Route>
          <Route path="/">
            <Home />
          </Route>
        </Switch>
      </>
    </Router>
  );
}

function Home() {
  return <HomePage />;
}
function Hosting() {
  return <Shared />;
}
function Reseller() {
  return <ResellerMain />;
}
function Affiliate() {
  window.scrollTo(0, 0);
  return <AffiliateHome />;
}
function VPS() {
  return <VpsMain />;
}
function Dedicate() {
  return <DedicateMain />;
}
function Window() {
  return <Windows />;
}
