import React from "react";
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";
// images 
import Logo from '../Images/pits_logo_header.png'
// pages 
import Main from "./Main";
import Technologies from "./Technologies";
import Products from './Products'
import Industries from "./Industries";
// company pages 
import Career from "./Company/Career";
import About from "./Company/About";
import Contact from "./Company/Contact";
// services pages 
import WebDevelopment from "./Services/Website";
import ECommerce from "./Services/ECommerce";
import SoftwareDevelopment from './Services/Software'
import Android from "./Services/MobileApps";
import Graphic from "./Services/Graphics";

export default function App() {
  return (
    <Router>
      <>
      <nav className="navbar navbar-expand-lg bg-white p-0">
          <div className="container px-5">
            <Link to="/">
              <a className="navbar-brand" href="#">
                <img alt="logo" src={Logo} className="my-2" />
              </a>
            </Link>
    <div className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
      <span className="text-d-blue display-1"><i className="fas fa-th-list"></i></span>
    </div>
    <div className="collapse navbar-collapse" id="navbarSupportedContent">
      <ul className="navbar-nav m-auto mb-lg-0">
      <li className="nav-item mx-2">
          <Link to="/products"><a className="nav-link fw-bold" href="#">Products</a></Link>
                </li>
        <li className="nav-item mx-2 dropdown">
          <a className="nav-link dropdown-toggle fw-bold" href="#" id="navbarDropdown" role="button" data-bs-toggle="dropdown" aria-expanded="false">
            Services
          </a>
          <ul className="dropdown-menu" aria-labelledby="navbarDropdown">
            <li><a className="dropdown-item fw-bold" href="https://ssls.pk" target="blank">SSl Certificates</a></li>
            <Link to="/ecommerce"><li><a className="dropdown-item fw-bold" href="#">E-commerce</a></li></Link>
            <Link to="/softwaredevelopment"><li><a className="dropdown-item fw-bold" href="#">Software</a></li></Link>
            <Link to="/android"><li><a className="dropdown-item fw-bold" href="#">Mobile Apps</a></li></Link>
            <Link to="/graphic"><li><a className="dropdown-item fw-bold" href="#">Graphics</a></li></Link>
            <li><a className="dropdown-item fw-bold" href="https://hosters.pk/">Web Hosting</a></li>
                    <Link to="/webdevelopment"><li><a className="dropdown-item fw-bold" href="#">Website</a></li></Link>
                  </ul>
                </li>
                <li className="nav-item mx-2">
          <Link to="/Technologies"><a className="nav-link fw-bold" href="#">Technologies</a></Link>
                </li>
                <li className="nav-item mx-2">
          <Link to="/Industries"><a className="nav-link fw-bold" href="#">Industries</a></Link>
                </li>
                <li className="nav-item mx-2 dropdown">
          <a className="nav-link dropdown-toggle fw-bold" href="#" id="navbarDropdown" role="button" data-bs-toggle="dropdown" aria-expanded="false">
            Company
          </a>
          <ul className="dropdown-menu" aria-labelledby="navbarDropdown">
            <Link to="/about"><li><a className="dropdown-item fw-bold" href="#">About</a></li></Link>
            <Link to="/contact"><li><a className="dropdown-item fw-bold" href="#">Contact</a></li></Link>
            <Link to="/career"><li><a className="dropdown-item fw-bold" href="#">Career</a></li></Link>
                  </ul>
                </li>
              </ul>
       <Link to="/contact"><button className="btn text-white bg-danger rounded-pill px-3 shadow p-2"><h6 className="m-0">GET A QUOTE</h6></button></Link>
    </div>
  </div>
        </nav>
        <Switch>
          <Route exact path="/" element={<Home />}/>
          <Route exact path="/technologies" element={<Technology />} />
          <Route exact path="/products" element={<Product />} />
          <Route exact path="/industries" element={<Industry />} />
          {/* services pages  */}
          <Route exact path="/webdevelopment" element={<Development />} />
          <Route exact path="/ecommerce" element={<Ecommerce />} />
          <Route exact path="/softwaredevelopment" element={<Software />} />
          <Route exact path="/android" element={<Application />} />
          <Route exact path="/graphic" element={<Designing />} />
          {/* company pages  */}
          <Route exact path="/about" element={<AboutPage />} />
          <Route exact path="/contact" element={<ContactPage />} />
          <Route exact path="/career" element={<CareerPage />} />
        </Switch> 
      </>
    </Router>
  );
}

function Home() {
  return <Main/>;
}
function Technology() {
  return <Technologies/>;
}
function Product() {
  return <Products/>;
}
function Industry() {
  return <Industries/>;
}
// services pages 
function Development() {
  return <WebDevelopment/>;
}
function Ecommerce() {
  return <ECommerce/>;
}
function Software() {
  return <SoftwareDevelopment/>;
}
function Application() {
  return <Android/>;
}
function Designing() {
  return <Graphic/>;
}
// company pages 
function AboutPage() {
  return <About/>;
}
function ContactPage() {
  return <Contact/>;
}
function CareerPage() {
  return <Career/>;
}
