import React from 'react'
// import $ from 'jquery'

const Test = () => {
    ;(function($){
        if (!$) {
          return;
        }
        $(document).ready(function(){
          var $parent = $('.parent-select-price');
          if (!$parent.length) {
            return;
          }
          var $select = $parent.find('select.select-price[data-target]');
          if (!$select.length) {
            return;
          }
          $select.each(function() {
            $(this).parent($parent).fadeIn();
            var $target = $(this).attr('data-target');
            if (!$target) {
              return;
            }
            $target = $('[data-price='+$target+']');
            if ($target.length==0) {
              return;
            }
            var $selected = $(this).find('option:selected');
            if ($selected.length) {
              $target.html($selected.val());
            }
            
            $(this).on('change', function(e) {
              e.stopPropagation();
              $selected = $(this).find('option:selected');
              $target.html($selected.val());
              $('#link_button-31-28884').attr('href',$selected.data('url'));
            });
          });
        });
    })(window.jQuery)
    return (
        <>
               <span data-price="plan1"></span>
  <div class="form-group parent-select-price">
  <select class="form-control select-price" data-target="plan1">
    <option value="126" class="cycle" data-url="https://hosters.pk/clientarea/index.php/store/flash-high-speed/unlimited?=1&billingcycle=monthly"  selected                                            >Monthly (Discount 10%)</option>
    <option value="126" class="cycle" data-url="https://hosters.pk/clientarea/index.php/store/flash-high-speed/unlimited?=1&billingcycle=quarterly">Quarterly (Discount 10%)</option>
    <option value="112" class="cycle" data-url="https://hosters.pk/clientarea/index.php/store/flash-high-speed/unlimited?=1&billingcycle=semiannually">Semi-Annually (Discount 20%)</option>
  <option value="105" class="cycle" data-url="https://hosters.pk/clientarea/index.php/store/flash-high-speed/unlimited?=1&billingcycle=annually">Annually (Discount 25%)</option>
<option value="98" class="cycle" data-url="https://hosters.pk/clientarea/index.php/store/flash-high-speed/unlimited?=1&billingcycle=biennually">Biennially (Discount 30%)</option>
<option value="70" class="cycle" data-url="https://hosters.pk/clientarea/index.php/store/flash-high-speed/unlimited?=1&billingcycle=triennually">Triennially (Discount 50%)</option>
</select>
</div>
<a id="link_button-31-28884" class="ct-link-button" href="https://hosters.pk/clientarea/index.php/store/flash-high-speed/unlimited?=1&billingcycle" target="_blank">Buy Now</a>
        </>
    )
}

export default Test
