import React from 'react'
import { Tab, Tabs, TabList, TabPanel } from 'react-tabs';
import Card1 from './Card2'
import Card2 from './Card2'
import Card3 from './Card3'
import $ from 'jquery'

const Cards = () => {
// Show the first tab and hide the rest
$('#tabs-nav li:first-child').addClass('active');
$('.tab-content').hide();
$('.tab-content:first').show();

// Click function
$('#tabs-nav li').click(function(){
  $('#tabs-nav li').removeClass('active');
  $(this).addClass('active');
  $('.tab-content').hide();
  
  var activeTab = $(this).find('a').attr('href');
  $(activeTab).fadeIn();
  return false;
});
    return (
      <>
{/* <!-- Tabs --> */}
<a href="#" class="tab active" data-toggle-target=".tab-content-1">Tab 1</a>
<a href="#" class="tab" data-toggle-target=".tab-content-2">Tab 2</a>

{/* <!-- Content --> */}
<div class="tab-content tab-content-1 active">
  <h1>Tab Content 1</h1>
</div>
<div class="tab-content tab-content-2">
  <h1>Tab Content 2</h1>
</div>
        {/* <!-- END tabs --> */}
        {/* card  */}
       <span data-price="plan1" className="text-white"></span>
        <section className="text-center">
        <div className="container">
          <h1 className="fw-bolder my-3 mx-5 ps-5">Wide range of <span className="text-green"> Hosting </span> Plans </h1>
          <h6 className="my-2 fw-bold"> Choose your Plans </h6>
      <Tabs className="p-0 m-0" id="controlled-tab-example">
        <TabList className="row m-auto mt-4 w-50 m-0 p-0 rounded-15">
                <Tab className="text-decoration-none col-lg-4 col-md-4 col-sm-12 m-0 p-0 rounded-15">
              <button className="clicked-green w-100 btn fw-bolder list-group-item"> SSD Hosting </button>
            </Tab>
                <Tab className="text-decoration-none col-lg-4 col-md-4 col-sm-12 p-0 m-0 rounded-15">
              <button className="clicked-green w-100 btn fw-bolder list-group-item"> Flash </button>
            </Tab>
                <Tab className="text-decoration-none col-lg-4 col-md-4 col-sm-12 p-0 m-0 rounded-15">
                <button className="clicked-green w-100 btn fw-bolder list-group-item"> Shared VPS </button>
            </Tab>
        </TabList>
        {/* #1 */}
        <TabPanel>
           <Card1/>
        </TabPanel>
        {/* #2 */}
        <TabPanel>
            <Card2/>
        </TabPanel>
        {/* #3 */}
        <TabPanel>
        <Card3/>
        </TabPanel>
          </Tabs>
          </div>
        </section>
      </>
    )
}

export default Cards
