import React from "react";
import { Switch } from "react-router";
import {
  BrowserRouter as Router,
  Route,
  Link
} from "react-router-dom";
// navbar images 
import Logo from '../src/images/logo.png'
// pages 
import HomePage from './Main'
import Shared from './Shared'
import AffiliateHome from "./AffiliateHome";
import ResellerMain from "./ResellerMain";
import DedicateMain from "./Dedicate";
import VpsMain from './VpsHome'
export default function App() {
    // <!--Start of Tawk.to Script-->
var Tawk_API=Tawk_API||{}, Tawk_LoadStart=new Date();
(function(){
var s1=document.createElement("script"),s0=document.getElementsByTagName("script")[0];
s1.async=true;
s1.src='https://embed.tawk.to/5d7e91079f6b7a4457e1cadb/default';
s1.charset='UTF-8';
s1.setAttribute('crossorigin','*');
s0.parentNode.insertBefore(s1,s0);
})();
// <!--End of Tawk.to Script-->
  return (
    <Router>
      <div>
        <nav>
                  {/* topbar  */}
          <div className="container-fluid row bg-green m-0 text-light  lh-lg">
            <div className="container m-0 p-0">
            <div className="row ps-5 m-0 p-0">
              <div className="col-lg-10 col-md-6 col-sm-12">
                <div className="row">
              <p className=" col-lg-3 col-md-12 m-0 p-0 top-pra fw-bolder"><i className="fas fa-phone-alt me-1"></i> Phone : 0304-11 12 13 9 </p>
              <p className=" col-lg-3 col-md-12 m-0 p-0 top-pra fw-bolder"><i className="fab fa-whatsapp me-1"></i> +923 111 66 111 2 </p>
        </div>
          </div>
          <div className="col-lg-2 col-md-6 col-sm-12 m-0 p-0">
        <div className="d-flex ps-5">
          <a className="text-decoration-none text-light" href="https://www.facebook.com/Hosters.pk"><p className="mx-2 py-0 my-0 top-pra"><i className="fab fa-facebook-f"></i></p></a>
          <a className="text-decoration-none text-light" href="https://www.youtube.com/channel/UCY1GvZd8wY1B-PbsvbuSZOg"><p className="mx-2 py-0 my-0 top-pra"><i className="fab fa-youtube"></i></p></a>
          <a href=""></a><p className="mx-2 py-0 my-0 top-pra"><i className="fab fa-linkedin-in"></i></p>
          <p className="mx-2 py-0 my-0 top-pra"><i className="fab fa-twitter"></i></p>
            </div>
          </div>
          </div>
          </div>
        </div>
        {/* navbar  */}
         <nav className="navbar navbar-expand-lg bg-white p-0 m-0 navbar-light">
                <div className="container-fluid text-center p-0">
                    <span className="navbar-brand p-0 m-0" href="#"> <Link to="/" className="text-decoration-none "><img alt="pictures" src={Logo} className="w-50"/></Link></span>
    <button className="navbar-toggler me-5" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
      <span className="navbar-toggler-icon text-green"></span>
    </button>
    <div className="collapse navbar-collapse me-5" id="navbarSupportedContent">
      <ul className="navbar-nav ms-auto mb-2 mb-lg-0">
           <li className="nav-item dropdown">
          <span className="nav-link text-dark" href="#" id="navbarDropdown" role="button" data-bs-toggle="dropdown" aria-expanded="false">
          Hosting <p className="fs-10px d-inline ms-1"> <i className="fas fa-chevron-down"></i> </p>
          </span>
          <ul className="dropdown-menu" aria-labelledby="navbarDropdown">
            <Link to="/hosting" className="text-decoration-none"><li><span className="dropdown-item " href="#">Shared Hosting</span></li></Link>
            <Link to="/reseller" className="text-decoration-none"><li><span className="dropdown-item " href="#">Reseller Hosting</span></li></Link>
            <Link to="/vps" className="text-decoration-none"><li><span className="dropdown-item " href="#">VPS Hosting</span></li></Link>
            <Link to="/dedicate" className="text-decoration-none"><li><span className="dropdown-item " href="#">Dedicated Hosting</span></li></Link>
          </ul>
        </li>
        <li className="nav-item">
          <span href="#" className="nav-link text-dark mx-2  text-decoration-none"><Link to={{ pathname: "https://hosters.pk/clientarea/cart.php?a=add&domain=register" }} className="text-decoration-none">Domain</Link></span>
        </li>
        <li className="nav-item">
          <span className="nav-link mx-2 " href="#"><Link to="/reseller" className="text-decoration-none">SS Resseller</Link></span>
         </li>
         <li className="nav-item">
          <span className="nav-link mx-2 " href="#" ><Link to="/affiliate" className="text-decoration-none">Affiliate</Link></span>
        </li>
        <li className="nav-item">
          <span href="https://hosters.pk/blog/" className="nav-link mx-2 er text-decoration-none"><Link to={{ pathname: "https://hosters.pk/blog/" }} target="_blank" className="text-decoration-none">Blog</Link></span>
        </li>
      </ul>
        <button className="btn nav-btn bg-green rounded-pill px-4  p-2 lh-lg text-white fw-bolder" type="submit"> <p className="m-0"><Link to={{ pathname: "https://hosters.pk/clientarea/" }} target="_top" className="text-decoration-none text-light">CLIENTAREA</Link></p></button>
    </div>
  </div>
        </nav>
        </nav>
        <Switch>
        <Route path="/dedicate">
            <Dedicate />
          </Route>
          <Route path="/vps">
            <VPS />
          </Route>
          <Route path="/Affiliate">
            <Affiliate />
          </Route>
          <Route path="/reseller">
            <Reseller />
          </Route>
          <Route path="/hosting">
            <Hosting />
          </Route>
          <Route path="/">
            <Home />
          </Route>
        </Switch>
      </div>
    </Router>
  );
}

function Home() {
  return <HomePage/>;
}
function Hosting() {
  return <Shared/>;
}
function Reseller() {
  return <ResellerMain/>;
}
function Affiliate() {
  window.scrollTo(0, 0);
  return <AffiliateHome />;
}
function VPS() {
  return <VpsMain/>;
}
function Dedicate() {
  return <DedicateMain/>;
}
