})();
// <!--End of Tawk.to Script-->

  return (
    <Loader
    type="Puff"
    color="#00BFFF"
    height={100}
    width={100}
    timeout={3000} //3 secs
  />,
    <Helmet>
<meta property="og:image" content={Logo} />
</Helmet>,
    <Router>
      <div>
        <nav>
                  {/* topbar  */}
          <div className="container-fluid row bg-green m-0 text-light  lh-lg">
            <div className="container m-0 p-0">
            <div className="row m-0 p-0">
              <div className="col-lg-10 col-md-6 col-sm-12">
                <div className="row top-row">
              <p className=" col-lg-3 col-md-12 m-0 p-0 top-pra fw-bolder"><i className="fas fa-phone-alt me-1"></i> Phone : 0304-11 12 13 9 </p>
              <p className=" col-lg-3 col-md-12 m-0 p-0 top-pra fw-bolder"><i className="fab fa-whatsapp me-1"></i> +923 111 66 111 2 </p>
        </div>
          </div>
          <div className="col-lg-2 col-md-6 col-sm-12 m-0 p-0">
        <div className="d-flex ps-5">
          <a className="text-decoration-none text-light" href="https://www.facebook.com/hoster.pk" target="_blank"><p className="mx-2 py-0 my-0 top-pra"><i className="fab fa-facebook-f"></i></p></a>
          <a className="text-decoration-none text-light" href="https://www.youtube.com/channel/UCY1GvZd8wY1B-PbsvbuSZOg" target="_blank"><p className="mx-2 py-0 my-0 top-pra"><i className="fab fa-youtube"></i></p></a>
          <a href="https://pk.linkedin.com/company/pitspk" className="text-white" target="_blank"><p className="mx-2 py-0 my-0 top-pra"><i className="fab fa-linkedin-in"></i></p></a>
            </div>
          </div>
          </div>
          </div>
        </div>
        {/* navbar  */}
         <nav className="navbar navbar-expand-lg bg-white p-0 m-0 navbar-light">
                <div className="container-fluid text-center p-0">
                <span className="navbar-brand p-0 m-0" href="#">
                  <Link to="/" className="text-decoration-none "><img alt="pictures" src={Logo} className="w-50" /></Link></span>
    <button className="navbar-toggler me-5" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
      <span className="navbar-toggler-icon text-green"></span>
    </button>
    <div className="collapse navbar-collapse me-5" id="navbarSupportedContent">
      <ul className="navbar-nav ms-auto mb-2 mb-lg-0">
           <li className="nav-item dropdown">
          <span className="nav-link text-dark" href="#" id="navbarDropdown" role="button" data-bs-toggle="dropdown" aria-expanded="false">
          Hosting <p className="fs-10px d-inline ms-1"> <i className="fas fa-chevron-down"></i> </p>
          </span>
          <ul className="dropdown-menu" aria-labelledby="navbarDropdown">
            <Link to="/hosting" className="text-decoration-none"><li><span className="dropdown-item " href="#">Shared Hosting</span></li></Link>
            <Link to="/reseller" className="text-decoration-none"><li><span className="dropdown-item " href="#">Reseller Hosting</span></li></Link>
            <Link to="/vps" className="text-decoration-none"><li><span className="dropdown-item " href="#">VPS Hosting</span></li></Link>
            <Link to="/dedicate" className="text-decoration-none"><li><span className="dropdown-item " href="#">Dedicated Hosting</span></li></Link>
          </ul>
        </li>
        <li className="nav-item">
          <span href="#" className="nav-link text-dark mx-2  text-decoration-none"><Link to={{ pathname: "https://hoster.pk/clientarea/cart.php?a=add&domain=register" }} className="text-decoration-none" target="_blank">Domain</Link></span>
        </li>
                  <li className="nav-item dropdown">
          <span className="nav-link text-dark" href="#" id="navbarDropdown" role="button" data-bs-toggle="dropdown" aria-expanded="false">
          Partners <p className="fs-10px d-inline ms-1"> <i className="fas fa-chevron-down"></i> </p>
          </span>
          <ul className="dropdown-menu" aria-labelledby="navbarDropdown">
            <Link to="/reseller" className="text-decoration-none"><li><span className="dropdown-item " href="#">Hosting Reseller</span></li></Link>
            <a href="https://SSLS.pk" className="text-decoration-none" target="_blank"><li><span className="dropdown-item " href="#">SSL Reseller</span></li></a>
            <Link to="/domain" className="text-decoration-none"><li><span className="dropdown-item " href="#">Domain Reseller</span></li></Link>
          </ul>
        </li>
         <li className="nav-item">
          <span className="nav-link mx-2 " href="#" >
          <Link to="/affiliate" className="text-decoration-none">
          Affiliate
          </Link>

                      </span>
        </li>
        <li className="nav-item">
          <span href="https://hoster.pk/blog/" className="nav-link mx-2 er text-decoration-none"><Link to={{ pathname: "https://hoster.pk/blog/" }} target="_blank" className="text-decoration-none">Blog</Link></span>
        </li>
      </ul>
        <button className="btn nav-btn bg-green rounded-pill px-4  p-2 lh-lg text-white fw-bolder" type="submit"> <p className="m-0"><Link to={{ pathname: "https://hoster.pk/clientarea/" }} target="_blank" className="text-decoration-none text-light">CLIENTAREA</Link></p></button>
    </div>
  </div>
        </nav>
        </nav>
        <Switch>
        <Route path="/dedicate">
            <Dedicate />
          </Route>
          <Route path="/vps">
            <VPS />
          </Route>
          <Route path="/affiliate">
            <Affiliate />
            </Route>
            <Route exact path="/domain" element={<Domain />} />
            <Route exact path="/domain" element={<Domain />} />
            <Route exact path="/domain" element={<Domain />} />
            <Route exact path="/domain" element={<Domain />} />
            <Route exact path="/reseller" element={<Reseller />} />
            <Route exact path="/hosting" element={<Hosting />} />
            <Route exact path="/affiliate" element={<Affiliate />} />
          <Route exact path="/" element={<Home />} />
        </Switch>
      </div>
    </Router>
  );
}

function Home() {
  return <HomePage/>;
}
function Hosting() {
  return <Shared/>;
}
function Reseller() {
  return <ResellerMain/>;
}
function Affiliate() {
  window.scrollTo(0, 0);
  return <AffiliateHome />;
}
function VPS() {
  return <VpsMain/>;
}
function Dedicate() {
  return <DedicateMain/>;
}
